package week2.day2;

public class palindrome {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a= 313;
		
		int s= 0;
		for(int i=a-1;i>=0;i--)
		{
			
			s=a;
			
		}
		System.out.println(s);
		if (a==s)
		{
			System.out.println("313 is palindrome");
		}
		else
		{
			System.out.println("313 is not a palindrome");
		}

	}


	}


